export default (state={events:[]},action) => {
  switch (action.type) {
    case 'FETCH_EVENT':
      console.log({...state,events:[...state.events,action.payload]});

      //payload is the response returned from the API
      return ({...state,events:[...state.events,action.payload]});
    default:
      return state;
  }
}
