import React from 'react';
import {connect} from 'react-redux';
import {fetchEvent} from '../actions';

class Event extends React.Component{

  //Event properties:
  //id
  //title
  //date
  //time
  //location
  //task list
  componentDidMount(){
    this.props.fetchEvent(this.props.eventId).catch(error=>{
      console.log(error);
    });
  }


  render(){
    return(
      <div>
        <table>
          <tbody>
            <tr>
              <td>test id:</td><td>{this.props.id}</td>
            </tr>
            <tr>
              <td>test title:</td><td>{this.props.title}</td>
            </tr>
            <tr>
              <td>test date:</td><td>{this.props.date}</td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}

const mapStateToProps = async (state,ownProps) => {
  console.log("state");
  console.log(state);
  console.log("ownProps");
  console.log(ownProps);
  const eventToShow = state.events.find((event) => {
    console.log(event.id);
    console.log(ownProps.eventId)
    return event.id==ownProps.eventId
  });
  console.log("eventToShow");
  console.log(eventToShow);
  return {
    id:eventToShow.id,
    title:state.events.title,
    date:state.events.date,
    time:state.events.time,
    location:state.events.location,
    tasklist:state.events.tasklist
  };
}

export default connect(mapStateToProps,{fetchEvent:fetchEvent})(Event);
