import React from 'react';
import {connect} from 'react-redux';
import {fetchTask} from '../actions';

class Task extends React.Component {

  componentDidMount(){
    this.props.fetchTask(this.props.taskId).catch(error=>{
      console.log(error);
    });
  }

  render(){
    console.log(this.props);
    console.log("state");
    console.log(this.state);
    return (
      <div>
        <table>
          <tbody>
            <tr>
              <td>Task:</td><td>{this.props.id}</td>
            </tr>
            <tr>
              <td>Description:</td><td>{this.props.description}</td>
            </tr>
            <tr>
              <td>Completed:</td><td>{this.props.completed?"yes":"no"}</td>
            </tr>
          </tbody>
        </table>
        <button >{this.props.completed?"Mark Incomplete":"Mark Complete"}</button>
      </div>
    );
  }
}

const mapStateToProps = (state,ownProps) => {

  return state.tasks[ownProps.taskId];
}

export default connect(mapStateToProps,{fetchTask:fetchTask})(Task);
