// Action creator
import dbCaller from '../apis/dbCaller';


export const fetchEvent = (id) => async (dispatch) => {
  const response = await dbCaller.get(`/events/${id}`);
  dispatch ({
    type:'FETCH_EVENT',
    payload: response.data
  });
};

export const fetchTask = (id) => async (dispatch) => {
  const response = await dbCaller.get(`/tasks/${id}`);
  dispatch ({
    type:'FETCH_TASK',
    payload: response.data
  });
};

export const markComplete = (id) => async (dispatch) => {
  const testUpdate={
    "description":"testputtask",
    "deadline":1560795026265,
    "assignee":"someone@example.com",
    "completed":false
  }
  const response = await dbCaller.put(`/tasks/${id}`,"test");
  dispatch ({
    type:'MARK_COMPLETE',
    payload: response.data
  });
};
