export default (state={},action) => {
  switch (action.type) {
    case 'FETCH_EVENT':
      //payload is the response returned from the API
      console.log(action.payload);
      console.log(state);
      return {...state,id:action.payload.id,title:action.payload.title};
    default:
      return state;
  }
}
