import React from 'react';
import {connect} from 'react-redux';
import {fetchTask} from '../actions';

class Task extends React.Component {

  componentDidMount(){
    this.props.fetchTask(this.props.taskId).catch(error=>{
      console.log(error);
    });
  }

  render(){
    console.log(this.props);
    return (
      <div>
        <table>
          <tr>
            <td>Task:</td><td>{this.props.id}</td>
          </tr>
          <tr>
            <td>Description:</td><td>{this.props.description}</td>
          </tr>
          <tr>
            <td>Completed:</td><td><input type="checkbox" checked={this.props.completed}></input></td>
          </tr>
        </table>
        <button>Mark Completed</button>
      </div>
    );
  }
}

const mapStateToProps = (state,ownProps) => {

  return state.tasks[ownProps.taskId];
}

export default connect(mapStateToProps,{fetchTask:fetchTask})(Task);
